import React, { Component } from 'react'
import axios from 'axios'
import MovieItemDetails from './movie_item_details'
import Cast from './movie_cast'

class MovieDetails extends Component {
  constructor (props) {
    super(props)
    this.state = {
      movieDetails: []
    }
  }

   fetchMovieDetails = () => {
    const itemId = this.props.match.params.id;
    const ROOT_URL = 'https://api.themoviedb.org/3/movie';
    const API_KEY = 'api_key=40d60badd3d50dea05d2a0e053cc96c3&language=en-US';

    axios.get(`${ROOT_URL}/${itemId}?${API_KEY}&append_to_response=credits`).then(res => {
      console.log(res.data);
      console.log(this.props.match.params.type)
      this.setState({ movieDetails: res.data })
    });
  }

  componentDidMount(){
    this.fetchMovieDetails()
  }
  render(){
    return (
      <div className='movie-details'>
        <MovieItemDetails movieDetails={ this.state.movieDetails } />
        <Cast movieDetails={ this.state.movieDetails } />
      </div>
    )
  }
}

export default MovieDetails
