import React from 'react'

import Carousel from '@brainhubeu/react-carousel'
import '@brainhubeu/react-carousel/lib/style.css'

const Cast = (props) => {
  const movieCast = props.movieDetails.map(r => (
      <div
        key={r.id} >
        <img src={`https://image.tmdb.org/t/p/w185/${r.profile_path}`} alt={r.title} className='top-movie-results-poster' />
        <a href='#t'>{r.name}</a>
      </div>

  ))
  return <div className='top-movie-results'>
    <h2 className='top-rated-header'>Cast</h2>
    <div>
      <Carousel infinite
        slidesPerPage={8}
        slidesPerScroll={3}
        arrows
        animationSpeed={1500}
        stopAutoPlayOnHover
        offset={50}
        itemWidth={225}
        clickToChange
        centered>{movieCast}</Carousel></div>
  </div>
}

export default Cast
