import React from 'react'

import Carousel from '@brainhubeu/react-carousel'
import '@brainhubeu/react-carousel/lib/style.css'

const MovieTrailers = (props) => {
  const movieTrailerResults = props.movieTrailers.map(r => (
      <div
        key={r.id} >
        <iframe width="700" height="400" key={r.id} title ={r.name} src={`https://www.youtube.com/embed/${r.key}`}></iframe>
      </div>
  ))
  return <div className='movie-details-trailer'>
    <h2 className='movie-details-trailer-heading'>Trailers</h2>
    <div>
      <Carousel infinite
        slidesPerPage={1}
        slidesPerScroll={1}
        arrows
        animationSpeed={1500}
        stopAutoPlayOnHover
        offset={150}
        itemWidth={600}
        clickToChange
        centered>{movieTrailerResults}</Carousel></div>
  </div>
}

export default MovieTrailers
