import React, { Component } from 'react'
import axios from 'axios'
import MovieItemDetails from './movie_item_details'
import MovieTrailers from './movie_trailers'

class MovieDetails extends Component {
  constructor (props) {
    super(props)
    this.state = {
      movieDetails: [],
      movieTrailers: [],
      movieCast: []
    }
  }

   fetchMovieDetails = () => {
    const itemId = this.props.match.params.id;
    const ROOT_URL = 'https://api.themoviedb.org/3/movie';
    const API_KEY = 'api_key=40d60badd3d50dea05d2a0e053cc96c3&language=en-US';

    axios.get(`${ROOT_URL}/${itemId}?${API_KEY}&append_to_response=credits`).then(res => {
      console.log(res.data);
      console.log(this.props.match.params.type)
      this.setState({ movieDetails: res.data })
    });
  }

  fetchMovieTrailers = () => {
    const itemId = this.props.match.params.id;
    const URL = 'https://api.themoviedb.org/3/movie';
    const key = 'api_key=40d60badd3d50dea05d2a0e053cc96c3&language=en-US';
    //https://api.themoviedb.org/3/movie/503314/videos?api_key=40d60badd3d50dea05d2a0e053cc96c3&language=en-US


    axios.get(`${URL}/${itemId}/videos?${key}`).then(res => {
      console.log(res.data.results);
      this.setState({ movieTrailers: res.data.results })
    })
  }

  fetchMovieCredits =() => {
    const itemId = this.props.match.params.id;
    const URL = 'https://api.themoviedb.org/3/movie';
    const key = 'api_key=40d60badd3d50dea05d2a0e053cc96c3&language=en-US';

    axios.get(`${URL}/${itemId}/credits?${key}`).then(res => {
      console.log(res.data)
      this.setState({ movieCast: res.data })
    })
  }

  componentDidMount(){
    this.fetchMovieDetails()
    this.fetchMovieTrailers()
    this.fetchMovieCredits()
  }
  render(){
    return (
      <div className='movie-details'>
        <MovieItemDetails movieDetails={ this.state.movieDetails } />
        <MovieTrailers movieTrailers={ this.state.movieTrailers } />
        {/* <Cast movieDetails={ this.state.movieDetails } /> */}
      </div>
    )
  }
}

export default MovieDetails
