import React from 'react'
import Carousel from '@brainhubeu/react-carousel'
import '@brainhubeu/react-carousel/lib/style.css'

const TopRatedMovies = (props) => {
  const topMovieResults = props.topMovies.map(r => (
    <li
      key={r.id} >
      <a href='#t' className='rating'><i className='fas fa-star fa-fw' />{r.vote_average}</a>
      <img src={`https://image.tmdb.org/t/p/w185/${r.poster_path}`} alt={r.title} className='top-movie-results-poster' />
    </li>
  ))
  return <ul className='top-movie-results'>
    <Carousel infinite
      slidesPerPage={8}
      slidesPerScroll={3}
      arrows={true}
      animationSpeed={1500}
      stopAutoPlayOnHover
      offset={50}
      itemWidth={250}
      clickToChange
      centered>{topMovieResults}</Carousel></ul>
}

export default TopRatedMovies
