import React, { Component } from 'react'
import axios from 'axios';
import MovieDetails from './movie_details'

class ItemDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      itemDetails: []
    };
  }

  fetchItemDetails = () => {
    const itemId = this.props.match.params.id;
    const ROOT_URL = 'https://api.themoviedb.org/3/movie';
    const API_KEY = 'api_key=40d60badd3d50dea05d2a0e053cc96c3&language=en-US';

    axios.get(`${ROOT_URL}/${itemId}?${API_KEY}`).then(res => {
      console.log(res.data);
      this.setState({ itemDetails: res.data })
    });
  };

  componentDidMount() {
    this.fetchItemDetails()
  }



  render()  {
      const movieDetails = this.state.itemDetails.map((value, key) => {
            return (
                <div>
                    <div className="col-md-4" key={key}>
                        <div className="dashboard-info">
                            {Object.keys(value).map((val, k) => {
                                return (<h4 k={k}>{val}</h4>)
                                })
                            }
                        </div>
                    </div>
                </div>
            )
        })

        return (

            <div>{movieDetails}</div>
        );    
  }
}

export default ItemDetails
