import React, { Component } from 'react'
import axios from 'axios';
import MovieDetails from './movie_details'

class ItemDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      itemDetails: [],
      tvDetails: []
    };
  }


  fetchData(id, type = this.props.match.params.type) {
    if (type === 'movie') {

    const itemId = this.props.match.params.id;
    const ROOT_URL = 'https://api.themoviedb.org/3/movie';
    const API_KEY = 'api_key=40d60badd3d50dea05d2a0e053cc96c3&language=en-US';

    axios.get(`${ROOT_URL}/${itemId}?${API_KEY}`).then(res => {
      console.log(res.data);
      this.setState({ itemDetails: res.data })
      });
    } else if (type === 'tv') {

    const tvId = this.props.match.params.id;
    const TV_URL = 'https://api.themoviedb.org/3/tv';
    const TV_KEY = 'api_key=40d60badd3d50dea05d2a0e053cc96c3&language=en-US';

    axios.get(`${TV_URL}/${tvId}?${TV_KEY}`).then(res => {
      console.log(res.data)
      this.setState({ tvDetails: res.data })
    })
    }
  }
