  }



  componentDidMount() {
    this.fetchData(this.props.match.params.id)
  }



  render()  {

   return (
     <div>
      <MovieDetails itemDetails={this.state.itemDetails}/>
     </div>
   )     
  }
}

export default ItemDetails
