import React from 'react'
import Carousel from '@brainhubeu/react-carousel'
import '@brainhubeu/react-carousel/lib/style.css'

const PopularMovies = (props) => {
  const PopularMovieResults = props.popularMovies.map(r => (
    <li
      key={r.id} >
      {/* <a href='#t' className='rating'><i className='fas fa-star fa-fw' />{r.vote_average}</a> */}
      <img src={`https://image.tmdb.org/t/p/w185/${r.poster_path}`} alt={r.title} className='top-movie-results-poster' />
    </li>
  ))
  return <div className='top-movie-results'>
    <h2 className='top-rated-header'>Popular Movies</h2>
    <ul>
      <Carousel infinite
        slidesPerPage={8}
        slidesPerScroll={3}
        arrows
        animationSpeed={1500}
        stopAutoPlayOnHover
        offset={50}
        itemWidth={225}
        clickToChange
        centered>{PopularMovieResults}</Carousel></ul>
  </div>
}

export default PopularMovies
