import React from 'react'
import ReactDOM from 'react-dom'

// import Carousel from '@brainhubeu/react-carousel'
// import '@brainhubeu/react-carousel/lib/style.css'
// import Carousel from 'nuka-carousel'
// import Slider from 'react-slick'
// import 'slick-carousel/slick/slick.css'
// import 'slick-carousel/slick/slick-theme.css'
import 'react-responsive-carousel/lib/styles/carousel.min.css'
import { Carousel } from 'react-responsive-carousel'

const headerImages = (props) => {
  const imageResults = props.trending.slice(0, 5).map(r => (
    <img key={r.id} src={`https://image.tmdb.org/t/p/w1280${r.backdrop_path}`} alt={r.title} className='header-image' />
  ))
  // var settings = {
  //   dots: true,
  //   infinite: true,
  //   speed: 500,
  //   slidesToShow: 1,
  //   slidesToScroll: 1,
  //   adaptiveHeight: true
  // }
  return <Carousel><div>{imageResults}</div></Carousel>
}
ReactDOM.render(<Carousel />, document.querySelector('.demo-carousel'));

export default headerImages
